

























CREATE VIEW OWNED_CARS
    AS
      SELECT C.NAME, F.BRAND, F.PRICE
      FROM CARS F, CLIENTS C
      WHERE C.PHONE_NUMBER = F.CLIENT_PHONE_NUMBER;


SELECT * FROM OWNED_CARS;



CREATE VIEW REQUIRED_BOUGHT_AUTOS
    AS
      SELECT C.NAME, R.TYPE_AUTOMOBILE, R.MAX_PRICE
      FROM REQUIRES R, CLIENTS C
      WHERE C.PHONE_NUMBER = R.CLIENT_PHONE_NUMBER;

SELECT * FROM REQUIRED_BOUGHT_AUTOS;

